import React, { useState } from 'react';
import './LoginSignup.css';

import user_icon from '../Assets/person.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';

const LoginSignup = () => {
  const [action, setAction] = useState("Sign Up");

  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    password: '',
    confirmPassword: '',
  });

  const [errors, setErrors] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    password: '',
    confirmPassword: '',
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrors({ ...errors, [name]: '' });
  };

  const validateFields = () => {
    let formErrors = {};
    if (action === 'Sign Up') {
      if (!formData.firstName) formErrors.firstName = 'First Name is required';
      if (!formData.lastName) formErrors.lastName = 'Last Name is required';
      if (!formData.phoneNumber) formErrors.phoneNumber = 'Phone Number is required';
      if (!formData.confirmPassword) formErrors.confirmPassword = 'Confirm Password is required';
      if (formData.password !== formData.confirmPassword) formErrors.confirmPassword = 'Passwords do not match';
    }
    if (!formData.email) formErrors.email = 'Email is required';
    if (action === 'Forgot Password' && !formData.password) {
      formErrors.password = 'New Password is required';
    }
    return formErrors;
  };

  const handleSignup = async () => {
    const formErrors = validateFields();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }

    try {
      const response = await fetch('http://localhost:9090/freshers/create', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          firstName: formData.firstName,
          lastName: formData.lastName,
          email: formData.email,
          phoneNumber: formData.phoneNumber,
          password: formData.password,
        }),
      });

      if (response.ok) {
        alert('Sign Up successful!');
        setAction('Login');
      } else {
        alert('Error during sign up');
      }
    } catch (error) {
      console.error('Error signing up:', error);
      alert('An error occurred during sign up');
    }
  };

  const handleLogin = async () => {
    const formErrors = validateFields();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }

    try {
      const response = await fetch('http://localhost:9090/freshers/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password,
        }),
      });

      if (response.ok) {
        alert('Login successful!');
      } else {
        alert('Invalid credentials');
      }
    } catch (error) {
      console.error('Error logging in:', error);
      alert('An error occurred during login');
    }
  };

  const handlePasswordReset = async () => {
    const formErrors = validateFields();
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      return;
    }

    try {
      const response = await fetch('http://localhost:9090/freshers/reset-password', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password, // new password
        }),
      });

      if (response.ok) {
        alert('Password reset successful!');
        setAction('Login');
      } else {
        alert('Error resetting password');
      }
    } catch (error) {
      console.error('Error resetting password:', error);
      alert('An error occurred during password reset');
    }
  };

  return (
    <form className='container'>
      <div className="header">
        <div className='text'>{action}</div>
        <div className="underline"></div>
      </div>

      <div className='inputs'>
        {action === "Sign Up" && (
          <>
            <div className={`input ${errors.firstName ? 'error' : ''}`}>
              <img src={user_icon} alt="User Icon" />
              <input
                type="text"
                placeholder='First Name *'
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
            </div>
            {errors.firstName && <div className="error-message">{errors.firstName}</div>}

            <div className={`input ${errors.lastName ? 'error' : ''}`}>
              <img src={user_icon} alt="User Icon" />
              <input
                type="text"
                placeholder='Last Name *'
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
            </div>
            {errors.lastName && <div className="error-message">{errors.lastName}</div>}
          </>
        )}

        <div className={`input ${errors.email ? 'error' : ''}`}>
          <img src={email_icon} alt="Email Icon" />
          <input
            type="email"
            placeholder='Email Id *'
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </div>
        {errors.email && <div className="error-message">{errors.email}</div>}

        {action === "Sign Up" && (
          <div className={`input ${errors.phoneNumber ? 'error' : ''}`}>
            <img src={user_icon} alt="Phone Icon" />
            <input
              type="text"
              placeholder='Phone Number *'
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleInputChange}
              required
            />
          </div>
        )}
        {action === "Sign Up" && errors.phoneNumber && <div className="error-message">{errors.phoneNumber}</div>}

        <div className={`input ${errors.password ? 'error' : ''}`}>
          <img src={password_icon} alt="Password Icon" />
          <input
            type="password"
            placeholder={action === "Forgot Password" ? 'New Password *' : 'Password *'}
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>
        {errors.password && <div className="error-message">{errors.password}</div>}

        {action === "Sign Up" && (
          <div className={`input ${errors.confirmPassword ? 'error' : ''}`}>
            <img src={password_icon} alt="Password Icon" />
            <input
              type="password"
              placeholder='Confirm Password *'
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleInputChange}
              required
            />
          </div>
        )}
        {action === "Sign Up" && errors.confirmPassword && <div className="error-message">{errors.confirmPassword}</div>}
      </div>

      {action === "Forgot Password" && (
        <div className="submit-container">
          <div className="submit" onClick={handlePasswordReset}>Reset Password</div>
        </div>
      )}

      {action === "Login" && <div className='forgot-password'>Forgot Password? <span onClick={() => setAction("Forgot Password")}>Click Here!</span></div>}

      <div className="navigation-links">
        {action === "Login" ? (
          <div className="link" onClick={() => setAction("Sign Up")}>New user?<span> Register</span></div>
        ) : (
          <div className="link" onClick={() => setAction("Login")}>Already have an account? <span>Log In</span></div>
        )}
      </div>
    </form>
  );
};

export default LoginSignup;
