import React, { useState } from 'react';
import './LoginSignup.css';

import user_icon from '../Assets/person.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';

const LoginSignup = () => {
  const [action, setAction] = useState("Sign Up");

  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: '',
  });

  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
    setErrors({ ...errors, [name]: '' }); // Clear error when user types
  };

  const validateFields = () => {
    const newErrors = {};

    if (action === "Sign Up") {
      if (!formData.firstName.trim()) newErrors.firstName = "First Name is required";
      if (!formData.lastName.trim()) newErrors.lastName = "Last Name is required";
      if (!formData.phone.trim()) newErrors.phone = "Phone Number is required";
      if (formData.password !== formData.confirmPassword) {
        newErrors.confirmPassword = "Passwords do not match";
      }
    }

    if (!formData.email.trim()) newErrors.email = "Email is required";
    if (!formData.password.trim()) newErrors.password = "Password is required";

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSignup = async () => {
    if (!validateFields()) return;

    try {
      const response = await fetch('   ', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          firstName: formData.firstName,
          lastName: formData.lastName,
          email: formData.email,
          phoneNumber: formData.phone,
          password: formData.password,
        }),
      });

      if (response.ok) {
        alert('Sign Up successful!');
        setAction('Login');
      } else {
        alert('Error during sign up');
      }
    } catch (error) {
      console.error('Error signing up:', error);
      alert('An error occurred during sign up');
    }
  };

  const handleLogin = async () => {
    if (!validateFields()) return;

    try {
      const response = await fetch('   ', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password,
        }),
      });

      if (response.ok) {
        alert('Login successful!');
      } else {
        alert('Invalid credentials');
      }
    } catch (error) {
      console.error('Error logging in:', error);
      alert('An error occurred during login');
    }
  };

  return (
    <form className='container'>
      <div className="header">
        <div className='text'>{action}</div>
        <div className="underline"></div>
      </div>

      <div className='inputs'>
        {action === "Sign Up" && (
          <>
            <div className='input'>
              <img src={user_icon} alt="User Icon" />
              <input
                type="text"
                placeholder='First Name'
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
              {errors.firstName && <span className="error">{errors.firstName}</span>}
            </div>

            <div className='input'>
              <img src={user_icon} alt="User Icon" />
              <input
                type="text"
                placeholder='Last Name'
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
              {errors.lastName && <span className="error">{errors.lastName}</span>}
            </div>
          </>
        )}

        <div className='input'>
          <img src={email_icon} alt="Email Icon" />
          <input
            type="email"
            placeholder='Email Id'
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
          {errors.email && <span className="error">{errors.email}</span>}
        </div>

        {action === "Sign Up" && (
          <div className='input'>
            <img src={user_icon} alt="Phone Icon" />
            <input
              type="text"
              placeholder='Phone Number'
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
            {errors.phone && <span className="error">{errors.phone}</span>}
          </div>
        )}

        <div className='input'>
          <img src={password_icon} alt="Password Icon" />
          <input
            type="password"
            placeholder='Password'
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
          {errors.password && <span className="error">{errors.password}</span>}
        </div>

        {action === "Sign Up" && (
          <div className='input'>
            <img src={password_icon} alt="Password Icon" />
            <input
              type="password"
              placeholder='Confirm Password'
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleInputChange}
              required
            />
            {errors.confirmPassword && <span className="error">{errors.confirmPassword}</span>}
          </div>
        )}
      </div>

      {action === "Login" && <div className='forgot-password'>Forgot Password? <span>Click Here!</span></div>}

      <div className="submit-container">
        {action === "Sign Up" ? (
          <div className="submit" onClick={handleSignup}>Create Account</div>
        ) : (
          <div className="submit" onClick={handleLogin}>Login</div>
        )}
      </div>

      <div className="navigation-links">
        {action === "Login" ? (
          <div className="link" onClick={() => setAction("Sign Up")}>New user?<span> Register</span></div>
        ) : (
          <div className="link" onClick={() => setAction("Login")}>Already have an account? <span>Log In</span></div>
        )}
      </div>
    </form>
  );
};

export default LoginSignup;
