import React, { useState } from 'react';
import './LoginSignup.css';

import user_icon from '../Assets/person.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';

const LoginSignup = () => {
  const [action, setAction] = useState("Sign Up");

  // State for form data
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: ''
  });

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle Sign Up API call
  const handleSignup = async () => {
    // Validate password confirmation
    if (formData.password !== formData.confirmPassword) {
      alert('Passwords do not match');
      return;
    }

    try {
      const response = await fetch('', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          firstName: formData.firstName,
          lastName: formData.lastName,
          email: formData.email,
          phone: formData.phone,
          password: formData.password,
        }),
      });

      if (response.ok) {
        alert('Sign Up successful!');
        setAction('Login'); 
      } else {
        alert('Error during sign up');
      }
    } catch (error) {
      console.error('Error signing up:', error);
      alert('An error occurred during sign up');
    }
  };

  return (
    <div className='container'>
      <div className="header">
        <div className='text'>{action}</div>
        <div className="underline"></div>
      </div>

      <div className='inputs'>
        {action === "Sign Up" && (
          <>
            <div className='input'>
              <img src={user_icon} alt="" />
              <input
                type="text"
                placeholder='First Name'
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
            </div>

            <div className='input'>
              <img src={user_icon} alt="" />
              <input
                type="text"
                placeholder='Last Name'
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
            </div>
          </>
        )}

        <div className='input'>
          <img src={email_icon} alt="" />
          <input
            type="email"
            placeholder='Email Id'
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
        </div>

        {action === "Sign Up" && (
          <div className='input'>
            <img src={user_icon} alt="" />
            <input
              type="text"
              placeholder='Phone Number'
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
          </div>
        )}

        <div className='input'>
          <img src={password_icon} alt="" />
          <input
            type="password"
            placeholder='Password'
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
        </div>

        {action === "Sign Up" && (
          <div className='input'>
            <img src={password_icon} alt="" />
            <input
              type="password"
              placeholder='Confirm Password'
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleInputChange}
              required
            />
          </div>
        )}
      </div>

      {action === "Login" && <div className='forgot-password'>Forgot Password? <span>Click Here!</span></div>}

      <div className="submit-container">
        {action === "Sign Up" ? (
          <div className="submit" onClick={handleSignup}>Submit</div>
        ) : (
          <div className="submit gray" onClick={() => { setAction("Sign Up") }}>Sign Up</div>
        )}

        {action === "Login" ? (
          <div className="submit" onClick={() => { setAction("Login") }}>Login</div>
        ) : (
          <div className="submit gray" onClick={() => { setAction("Sign Up") }}>Login</div>
        )}
      </div>

      <div className="navigation-links">
        {action === "Login" ? (
          <div className="link" onClick={() => setAction("Sign Up")}>New user?<span> Register</span></div>
        ) : (
          <div className="link" onClick={() => setAction("Login")}>Already have an account? <span>Sign In</span></div>
        )}
      </div>
    </div>
  );
};

export default LoginSignup;
