import React, { useState } from 'react';
import './LoginSignup.css';

import user_icon from '../Assets/person.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';

const LoginSignup = () => {
  const [action, setAction] = useState("Sign Up");

  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: ''
  });

  const [errors, setErrors] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phone: '',
    password: '',
    confirmPassword: ''
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const validateForm = () => {
    let valid = true;
    let errorMessages = { ...errors };

    if (!formData.firstName) {
      errorMessages.firstName = 'First Name is required';
      valid = false;
    } else {
      errorMessages.firstName = '';
    }

    if (!formData.lastName) {
      errorMessages.lastName = 'Last Name is required';
      valid = false;
    } else {
      errorMessages.lastName = '';
    }

    if (!formData.email) {
      errorMessages.email = 'Email is required';
      valid = false;
    } else {
      errorMessages.email = '';
    }

    if (action === "Sign Up" && !formData.phone) {
      errorMessages.phone = 'Phone number is required';
      valid = false;
    } else {
      errorMessages.phone = '';
    }

    if (!formData.password) {
      errorMessages.password = 'Password is required';
      valid = false;
    } else {
      errorMessages.password = '';
    }

    if (action === "Sign Up" && formData.password !== formData.confirmPassword) {
      errorMessages.confirmPassword = 'Passwords do not match';
      valid = false;
    } else if (!formData.confirmPassword) {
      errorMessages.confirmPassword = 'Confirm Password is required';
      valid = false;
    } else {
      errorMessages.confirmPassword = '';
    }

    setErrors(errorMessages);
    return valid;
  };

  const handleSignup = async () => {
    if (!validateForm()) {
      alert('Please fix the errors and try again');
      return;
    }

    try {
      const response = await fetch('   ', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          firstName: formData.firstName,
          lastName: formData.lastName,
          email: formData.email,
          phoneNumber: formData.phone,
          password: formData.password,
        }),
      });

      if (response.ok) {
        alert('Sign Up successful!');
        setAction('Login');
      } else {
        alert('Error during sign up');
      }
    } catch (error) {
      console.error('Error signing up:', error);
      alert('An error occurred during sign up');
    }
  };

  const handleLogin = async () => {
    if (!formData.email || !formData.password) {
      alert('Please fill in both email and password');
      return;
    }

    try {
      const response = await fetch('   ', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: formData.email,
          password: formData.password,
        }),
      });

      if (response.ok) {
        alert('Login successful!');
      } else {
        alert('Invalid credentials');
      }
    } catch (error) {
      console.error('Error logging in:', error);
      alert('An error occurred during login');
    }
  };

  return (
    <form className='container'>
      <div className="header">
        <div className='text'>{action}</div>
        <div className="underline"></div>
      </div>

      <div className='inputs'>
        {action === "Sign Up" && (
          <>
            <div className='input'>
              <img src={user_icon} alt="User Icon" />
              <input
                type="text"
                placeholder='First Name'
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
              {errors.firstName && <div className="error">{errors.firstName}</div>}
            </div>

            <div className='input'>
              <img src={user_icon} alt="User Icon" />
              <input
                type="text"
                placeholder='Last Name'
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
              {errors.lastName && <div className="error">{errors.lastName}</div>}
            </div>
          </>
        )}

        <div className='input'>
          <label htmlFor='firstName'><span className="required">*</span></label>
          <img src={email_icon} alt="Email Icon" />
          <input
            type="email"
            placeholder='Email Id'
            name="email"
            value={formData.email}
            onChange={handleInputChange}
            required
          />
          {errors.email && <div className="error">{errors.email}</div>}
        </div>

        {action === "Sign Up" && (
          <div className='input'>
            <img src={user_icon} alt="Phone Icon" />
            <input
              type="text"
              placeholder='Phone Number'
              name="phone"
              value={formData.phone}
              onChange={handleInputChange}
              required
            />
            {errors.phone && <div className="error">{errors.phone}</div>}
          </div>
        )}

        <div className='input'>
          <img src={password_icon} alt="Password Icon" />
          <input
            type="password"
            placeholder='Password'
            name="password"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
          {errors.password && <div className="error">{errors.password}</div>}
        </div>

        {action === "Sign Up" && (
          <div className='input'>
            <img src={password_icon} alt="Password Icon" />
            <input
              type="password"
              placeholder='Confirm Password'
              name="confirmPassword"
              value={formData.confirmPassword}
              onChange={handleInputChange}
              required
            />
            {errors.confirmPassword && <div className="error">{errors.confirmPassword}</div>}
          </div>
        )}
      </div>

      {action === "Login" && <div className='forgot-password'>Forgot Password? <span>Click Here!</span></div>}

      <div className="submit-container">
        {action === "Sign Up" ? (
          <div className="submit" onClick={handleSignup}>Create Account</div>
        ) : (
          <div className="submit" onClick={handleLogin}>Login</div>
        )}
      </div>

      <div className="navigation-links">
        {action === "Login" ? (
          <div className="link" onClick={() => setAction("Sign Up")}>New user?<span> Register</span></div>
        ) : (
          <div className="link" onClick={() => setAction("Login")}>Already have an account? <span>Log In</span></div>
        )}
      </div>
    </form>
  );
};

export default LoginSignup;
